package strings;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.HashMap;

/**
 * @author PRATAP LeetCode 1044
 *
 */
public class LongestDuplicateSubstring {

	/**
	 * @param args
	 * @throws IOException 
	 */
	public static void main(String[] args) throws IOException {
		// TODO Auto-generated method stub
//		String s = "";
//		String s = "nnpxouomcofdjuujloanjimymadkuepightrfodmauhrsy";
//		String s = "";
//		String s = "banana";
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String s = br.readLine();
		LongestDuplicateSubstring obj = new LongestDuplicateSubstring();
		String ans = obj.longestDupSubstring(s);
		System.out.println(ans);
		String ea = "";
		System.out.println(ans.length() + " " + ea.length());

	}

	String ans = "";

	public String longestDupSubstring(String s) {
		int low = 0, high = s.length();
		while (low <= high) {
			int mid = low + (high - low) / 2;
			boolean check = findSub(s, mid);
			if (check) {
				low = mid + 1;
			} else {
				high = mid - 1;
			}
		}
		return ans;
	}

	private boolean findSub(String s, int mid) {
		// TODO Auto-generated method stub
		long hash = 0, mod = 1L << 39;
		long p = 31 , pow = 1;
		for (int i = mid - 1; i >= 0; i--) {
			long ch = s.charAt(i) - 'a' + 1;
			hash = (hash + ch * pow) % mod;
			if (i > 0)
				pow = (pow * p) % mod;
		}
		HashMap<Long, Integer> map = new HashMap<Long, Integer>();
		map.put(hash, 0);
		for (int i = mid; i < s.length(); i++) {
			long cChar = s.charAt(i) - 'a' + 1;
			long dChar = s.charAt(i - mid) - 'a' + 1;
			hash = (hash - (dChar * pow) % mod + mod) % mod;
			hash = (hash * p) % mod;
			hash = (hash + cChar) % mod;
			if (map.containsKey(hash)) {
				ans = s.substring(map.get(hash), map.get(hash) + mid);
				return true;
			} else {
				map.put(hash, i - mid + 1);
			}
		}
		return false;
	}
}
